 var express=require("express");
module.exports=function(){
var app=express();
	app.set('port', process.env.PORT || 8888);

	app.use(express.static('public'));

	app.engine('html', require('ejs').renderFile);

	app.get('/', function(req, res) {
			res.render("./index.html");
	});
return app;

}